package simplepatterns;

public class AsciiDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 7 bits
		// 2^7=128
		// 0 - 127
		// ASCII - American Standard Code for Information Interchange
		
		for(int i=0; i<=127; i++){
			
			System.out.printf("%d : %c \n", i,i);
		}

	}

}
